import React, { useState } from 'react';
import { Container, Form, Header, Button } from 'semantic-ui-react';
import axios from 'axios';
import backgroundImage from '../images/background.jpg';

const LandlordSignup = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    password_confirmation: '',
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('/api/landlords', { landlord: formData })
      .then((response) => {
        // Handle successful form submission
        console.log(response.data);
      })
      .catch((error) => {
        // Handle form submission errors
        setErrors(error.response.data.errors);
      });
  };

  return (
    <div style={{
      backgroundImage: `url(${backgroundImage})`,
      backgroundPosition: 'center',
      backgroundSize: 'cover',
      height: '100vh',
    }}>
      <Container text>
        <Header as='h2'>Sign up as a Landlord</Header>
        <Form onSubmit={handleSubmit}>
          <Form.Input
            label='Name'
            name='name'
            value={formData.name}
            onChange={handleInputChange}
            error={errors.name && { content: errors.name[0] }}
          />
          <Form.Input
            label='Email'
            name='email'
            value={formData.email}
            onChange={handleInputChange}
            error={errors.email && { content: errors.email[0] }}
          />
          <Form.Input
            label='Password'
            name='password'
            type='password'
            value={formData.password}
            onChange={handleInputChange}
            error={errors.password && { content: errors.password[0] }}
          />
          <Form.Input
            label='Confirm Password'
            name='password_confirmation'
            type='password'
            value={formData.password_confirmation}
            onChange={handleInputChange}
            error={
              errors.password_confirmation && {
                content: errors.password_confirmation[0],
              }
            }
          />
          <Button type='submit'>Submit</Button>
        </Form>
      </Container>
    </div>
  );
};

export default Signup;
