import React, { useState, useEffect } from 'react';
import { Button, Icon, Input, List } from 'semantic-ui-react';
import axios from 'axios';

const ToDoList = ({ landlordId }) => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  useEffect(() => {
    // Fetch tasks from the API when the component mounts
    axios.get(`/api/landlords/${landlordId}/tasks`)
      .then(response => {
        setTasks(response.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, [landlordId]);

  const addTask = () => {
    // Create a new task and add it to the list of tasks in state
    axios.post(`/api/landlords/${landlordId}/tasks`, { description: newTask })
      .then(response => {
        setTasks([...tasks, response.data]);
        setNewTask('');
      })
      .catch(error => {
        console.log(error);
      });
  };

  const deleteTask = (taskId) => {
    // Delete a task from the list of tasks in state
    axios.delete(`/api/landlords/${landlordId}/tasks/${taskId}`)
      .then(() => {
        setTasks(tasks.filter(task => task.id !== taskId));
      })
      .catch(error => {
        console.log(error);
      });
  };

  const updateTask = (updatedTask) => {
    // Update a task in the list of tasks in state
    axios.put(`/api/landlords/${landlordId}/tasks/${updatedTask.id}`, updatedTask)
      .then(() => {
        const updatedTasks = tasks.map(task => {
          if (task.id === updatedTask.id) {
            return updatedTask;
          } else {
            return task;
          }
        });
        setTasks(updatedTasks);
      })
      .catch(error => {
        console.log(error);
      });
  };

  const handleTaskInputChange = (event) => {
    // Update the newTask state when the user types in the input field
    setNewTask(event.target.value);
  };

  const handleTaskCheckboxChange = (event, taskId) => {
    // Update the completed status of a task when the user clicks the checkbox
    const updatedTask = tasks.find(task => task.id === taskId);
    updatedTask.completed = event.target.checked;
    updateTask(updatedTask);
  };

  return (
    <div>
      <h2>To-Do List</h2>
      <Input
        placeholder="Add task..."
        value={newTask}
        onChange={handleTaskInputChange}
        action={
          <Button icon onClick={addTask}>
            <Icon name="plus" />
          </Button>
        }
      />
      <List divided relaxed>
        {tasks.map(task => (
          <List.Item key={task.id}>
            <List.Content floated="right">
              <Button icon onClick={() => deleteTask(task.id)}>
                <Icon name="trash" />
              </Button>
            </List.Content>
            <List.Content>
              <List.Header>
                <Input
                  type="checkbox"
                  checked={task.completed}
                  onChange={(event) => handleTaskCheckboxChange(event, task.id)}
                />
                {task.description}
              </List.Header>
            </List.Content>
          </List.Item>
        ))}
      </List>
    </div>
  );
}

export default ToDoList
