import React, { useState } from "react";
import { Form, Input, Dropdown, Checkbox, Button, Table } from "semantic-ui-react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

const options = [
  { key: "personal", value: "Personal", text: "Personal" },
  { key: "work", value: "Work", text: "Work" },
];

const priorityOptions = [
  { key: "low", value: "Low", text: "Low" },
  { key: "medium", value: "Medium", text: "Medium" },
  { key: "high", value: "High", text: "High" },
];

const TodoList = () => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [priority, setPriority] = useState("");
  const [dueDate, setDueDate] = useState(null);
  const [todos, setTodos] = useState([]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const newTodo = { title, description, category, priority, dueDate };
    setTodos([...todos, newTodo]);
    setTitle("");
    setDescription("");
    setCategory("");
    setPriority("");
    setDueDate(null);
  };

  const handleDelete = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  return (
    <div>
      <h2>Todo List</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Field>
          <label>Title</label>
          <Input
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Description</label>
          <Input
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Category</label>
          <Dropdown
            placeholder="Select a category"
            fluid
            selection
            options={options}
            value={category}
            onChange={(e, data) => setCategory(data.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Priority</label>
          <Dropdown
            placeholder="Select a priority"
            fluid
            selection
            options={priorityOptions}
            value={priority}
            onChange={(e, data) => setPriority(data.value)}
          />
        </Form.Field>
        <Form.Field>
          <label>Due Date</label>
          <DatePicker
            selected={dueDate}
            onChange={(date) => setDueDate(date)}
            minDate={new Date()}
            showTimeSelect
            dateFormat="MMMM d, yyyy h:mm aa"
            placeholderText="Click to select a due date"
          />
        </Form.Field>
        <Button type="submit">Add Todo</Button>
      </Form>
      <Table celled>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Title</Table.HeaderCell>
            <Table.HeaderCell>Description</Table.HeaderCell>
            <Table.HeaderCell>Category</Table.HeaderCell>
            <Table.HeaderCell>Priority</Table.HeaderCell>
            <Table.HeaderCell>Due Date</Table.HeaderCell>
            <Table.HeaderCell>Complete?</Table.HeaderCell>
            <Table.HeaderCell>Action</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        </Table>
        </div>
  );
}

export default To
